import classes from './Cart.module.css'
import CartItem from './CartItem'

import CartContext from '../../store/cart-context'
import { useContext, useState } from 'react'

function Cart(){
    const ctxCart=useContext(CartContext)
    //add ctxCart Instead of the static
    console.log(ctxCart)
    let  cartItem


     cartItem=ctxCart.items.map((item,i)=> <CartItem name={item.name} price={item.price} img={item.productImage} key={i} quant={item.quantity} totalAmt={ctxCart.totalAmount} />)

    return (
        <div className={classes.cart}>
            <h2>Cart</h2>
            <hr className={classes.hr}/>
            {cartItem}
        </div>
    )
}

export default Cart
