import Navigation from "./Components/Layout/Navigation";
// import ProductImage from "./Components/ProductImage/ProductImage";
import React, { useState } from "react";
import Container from "./Components/Layout/Container";
import Row from "./Components/Layout/Row";
import ProductDetail from "./Components/ProductDeatil/ProductDetail";
import Modal from "./Components/UI/Modal";
import useImage from "./Components/ProductImage/UseImage";




function App() {
  const DUMMY_PRODUCT = [
    {
      id: "p1",
      company: "sneaker company",
      name: "Fall Limited Edition Sneaker",
      description:
        "These low-profile sneakers are your perfect casual wear companion. Featuring a durable rubber outer sole they will withstand everything the weather can offer",
      price: 125.0,
      discount: 50,
      mrpPrice: 250.0,
      img:useImage('product-1-thumbnail')

    }
  ];

  const [openModal,setOpenModal]=useState(false)

  const openLightBox=()=>{
    setOpenModal(true)
  }
  const products = DUMMY_PRODUCT.map((productD) => (
    <ProductDetail
    openModalLightBox={openLightBox}
      key={productD.id}
      id={productD.id}
      company={productD.company}
      name={productD.name}
      description={productD.description}
      price={productD.price}
      discount={productD.discount}
      mrp={productD.mrpPrice}
      thumbnail1={productD.img}
    />
  ));


  return (
    <React.Fragment>
      <Navigation />
      {openModal && <Modal/> }
      <hr />
      <Container>
        <Row>{products}</Row>
      </Container>
    </React.Fragment>
  );
}

export default App;
