import React, { useState } from "react"
import useImage from "./UseImage"



function ProductImage(props) {
  const [defaultImage,setDefaultImage]=useState(props.imgHead)
  const [imgFileName,setImgFileName]=useState('')

  const { loading, error, image } = useImage('product-2')

  const clickHandler=(e)=>{
    setImgFileName(e.target.alt)
    if(e.target.alt==='product-1'){
      setDefaultImage(imgFileName)

    }
  }


  return (

    <React.Fragment>
      <img alt='mobile-product' src={defaultImage} className="img-fluid prod" />
      <div className="previous">
        <svg width="12" height="18" xmlns="http://www.w3.org/2000/svg"><path d="M11 1 3 9l8 8" stroke="#1D2026" stroke-width="3" fill="none" fill-rule="evenodd" /></svg>

      </div>
      <div className="next">
        <svg width="13" height="18" xmlns="http://www.w3.org/2000/svg"><path d="m2 1 8 8-8 8" stroke="#1D2026" stroke-width="3" fill="none" fill-rule="evenodd" /></svg>
      </div>

      custom react component for thumbnail
      passing props

      <div className="prod-thumbnail">
        <img alt="product-1" className="img-fluid col-2 col-lg-2" onClick={clickHandler} src={props.pic1} />
        <img alt="product-2" className="img-fluid col-2 col-lg-2" onClick={clickHandler} src={props.pic2} />
        <img alt="product-3" className="img-fluid col-2 col-lg-2" onClick={clickHandler} src={props.pic3} />
        <img alt="product-4" className="img-fluid col-2 col-lg-2" onClick={clickHandler} src={props.pic4} />


      </div>
    </React.Fragment>


  )

}

export default ProductImage
