import classes from './Cart.module.css'
import CartItem from './CartItem'
import image from '../../asset/image-product-1-thumbnail.jpg'

import CartContext from '../../store/cart-context'
import { useContext } from 'react'
function Cart(){
    const ctxCart=useContext(CartContext)
    //add ctxCart Instead of the static
    console.log(ctxCart.items)

    const cartItem=ctx.items.map((item,i)=> <CartItem name={item.name} price={item.price} img={item.productImage} key={i} />)

    return (
        <div className={classes.cart}>
            <h2>Cart</h2>
            <hr className={classes.hr}/>
            {cartItem}


        </div>
    )
}

export default Cart
