import React, { useContext, useState } from "react"
import ProductImage from "../ProductImage/ProductImage"
import ProductQuantityForm from "./ProductQuantityForm"
import tbproduct1 from "../../asset/image-product-1-thumbnail.jpg"
import tbproduct2 from "../../asset/image-product-2-thumbnail.jpg"
import tbproduct3 from "../../asset/image-product-3-thumbnail.jpg"
import tbproduct4 from "../../asset/image-product-4-thumbnail.jpg"
import mobile_product from '../../asset/image-product-1.jpg'
import CartContext from "../../store/cart-context"
import Modal from "../UI/Modal"


function ProductDetail(props) {
  const ctxCart=useContext(CartContext)
  const [invalidClass,setInValidClass]=useState('')

  const handleChange=()=>{
    setInValidClass('')
  }
  const getQuantity=(amt)=>{

    if(amt === 0) {
      setInValidClass('animate__shakeX')
    }else{
      setInValidClass('')
    }
    ctxCart.addItem({
      id:props.id,
      name:props.name,
      price:props.price,
      quantity:amt,
      productImage:tbproduct1
    })



  }
  const openModal=()=>{
    props.openModalLightBox()
  }
  return (
  <React.Fragment>
    <Modal>
     <div className="col-sm-12 col-md-6 col-lg-5 p-xl-5 banner-product__image">
       <ProductImage onModal={openModal} imgHead={mobile_product} pic1={tbproduct1} pic2={tbproduct2} pic3={tbproduct3} pic4={tbproduct4} />
     </div>
    <div className="col-sm-12 col-md-6 col-lg-5 banner-content p-3 p-lg-5">
      <div>
        <p className="decorative-text">{props.company}</p>
        <h1 className="product-header">{props.name}</h1>
        <p className="product-description">{props.description}
        </p>
      </div>
      <div className="price__discount">
        <div className="inner_price--discount">
          <h2 className="me-3">${props.price}</h2>
          <h5>{props.discount}%</h5>
        </div>
        <p>${props.mrp}</p>
      </div>

      <ProductQuantityForm onChangeClass={handleChange} invalidCls={invalidClass} onGetQuantity={getQuantity} />

    </div>
    </Modal>
    </React.Fragment>
  )
}

export default ProductDetail
