import Navigation from './Components/Layout/Navigation'
import ProductImage from "./Components/ProductImage/ProductImage";
import React from 'react'
import Container from "./Components/Layout/Container";
import Row from "./Components/Layout/Row";
import ProductDetail from './Components/ProductDeatil/ProductDetail';

const DUMMY_PRODUCT = [{
  id:'p1',
  company: 'sneaker company',
  name: 'Fall Limited Edition Sneaker',
  description: 'These low-profile sneakers are your perfect casual wear companion. Featuring a durable rubber outer sole they will withstand everything the weather can offer',
  price: 125.00,
  discount: 50,
  mrpPrice: 250.00

}]

function App() {
  const products=DUMMY_PRODUCT.map((productD=>(<ProductDetail id={productD.id} company={productD.company} name={DUMMY_PRODUCT.name} description={DUMMY_PRODUCT.description} price={DUMMY_PRODUCT.price} discount={DUMMY_PRODUCT.discount} mrp={DUMMY_PRODUCT.mrpPrice}/>)))
  return (
    <React.Fragment>
      <Navigation />
      <hr />
      <Container>
        <Row>
          <ProductImage />

         {products}
        </Row>
      </Container>
    </React.Fragment>
  );
}

export default App;
